import React, { useEffect, useState } from "react";
import { XIcon, ChevronRightIcon, RefreshIcon, LoadingIcon } from "./Icons";

const AdModal = ({
  isOpen,
  onClose,
  onSubmit,
  accounts,
  categories,
  onRefreshCategories,
  loadingCategories,
  extraFields,
  extraFieldValues,
  setExtraFieldValues,
  loadingExtraFields,
  newAd,
  setNewAd,
  adImages,
  setAdImages,
  categoriesUpdatedAt
}) => {
  if (!isOpen) return null;

  const [categoryPath, setCategoryPath] = useState([]);
  const [localCategories, setLocalCategories] = useState([]);

  const inputStyle = {
    width: "100%",
    padding: "12px 14px",
    border: "1px solid rgba(148,163,184,0.3)",
    borderRadius: "12px",
    background: "#0f172a",
    fontSize: "14px",
    color: "#e2e8f0",
    transition: "all 0.3s ease"
  };

  const labelStyle = {
    display: "block",
    marginBottom: "8px",
    fontWeight: "600",
    color: "#e2e8f0"
  };

  const formatAccountLabel = (account) => {
    const name = account.profileName || account.username || "Аккаунт";
    const email = account.profileEmail || "";
    return email ? `${name} (${email})` : name;
  };

  const findNode = (nodes, value) => {
    if (!value) return null;
    for (const node of nodes) {
      if (String(node.id) === String(value) || String(node.url) === String(value)) return node;
      if (node.children?.length) {
        const found = findNode(node.children, value);
        if (found) return found;
      }
    }
    return null;
  };

  const getBreadcrumbForPath = (path) => {
    const breadcrumb = [];
    let current = localCategories;
    for (const pathItem of path) {
      const found = current.find((cat) =>
        (cat.id && String(cat.id) === String(pathItem)) ||
        (cat.url && String(cat.url) === String(pathItem))
      );
      if (!found) break;
      breadcrumb.push(found);
      current = found.children || [];
    }
    return breadcrumb;
  };

  const resolveCategorySelection = (path) => {
    const breadcrumb = getBreadcrumbForPath(path);
    const selectedNode = breadcrumb.length ? breadcrumb[breadcrumb.length - 1] : null;
    const numericNode = [...breadcrumb].reverse().find((node) => /^\d+$/.test(String(node?.id || "")));
    return {
      selectedNode,
      numericNode: numericNode || selectedNode
    };
  };

  useEffect(() => {
    setLocalCategories(categories || []);
  }, [categories]);

  useEffect(() => {
    if (isOpen) {
      setCategoryPath([]);
      setNewAd((prev) => ({
        ...prev,
        categoryId: "",
        categoryUrl: ""
      }));
    }
  }, [isOpen, setNewAd]);

  useEffect(() => {
    if (!isOpen) return;
    if (!newAd.accountId) return;
    if (!categoryPath.length) return;
    const lastId = categoryPath[categoryPath.length - 1];
    const node = findNode(localCategories, lastId);
    if (node && (!node.children || node.children.length === 0)) {
      requestChildren(node);
    }
  }, [isOpen, newAd.accountId, categoryPath, localCategories]);

  const getCurrentCategories = () => {
    if (categoryPath.length === 0) return localCategories;

    let current = localCategories;
    for (const pathItem of categoryPath) {
      const found = current.find((cat) =>
        (cat.id && String(cat.id) === String(pathItem)) ||
        (cat.url && String(cat.url) === String(pathItem))
      );
      if (!found) return [];
      current = found.children || [];
    }
    return current;
  };

  const getSelectedCategory = () => {
    if (categoryPath.length === 0) return null;
    const lastId = categoryPath[categoryPath.length - 1];
    return findNode(localCategories, lastId);
  };

  const updateCategorySelection = async (category) => {
    if (!newAd.accountId) {
      alert("Сначала выберите аккаунт");
      return;
    }
    const value = category.id || category.url || "";
    if (!value) return;

    // Если категория уже в пути, возвращаемся к ней
    const indexInPath = categoryPath.findIndex(id => id === value);
    if (indexInPath !== -1) {
      const nextPath = categoryPath.slice(0, indexInPath + 1);
      setCategoryPath(nextPath);

      // Обновляем выбранную категорию
      const { selectedNode, numericNode } = resolveCategorySelection(nextPath);
      setNewAd((prev) => ({
        ...prev,
        categoryId: numericNode?.id ? String(numericNode.id) : "",
        categoryUrl: numericNode?.url || selectedNode?.url || ""
      }));
      return;
    }

    // Добавляем категорию в путь
    const newPath = [...categoryPath, value];
    setCategoryPath(newPath);

    // ВСЕГДА пытаемся загрузить детей, даже если они есть
    // (это обеспечит загрузку 3+ уровней)
    await requestChildren(category);

    // Обновляем выбранную категорию в форме
    const { selectedNode, numericNode } = resolveCategorySelection(newPath);
    setNewAd((prev) => {
      const next = { ...prev };
      next.categoryId = numericNode?.id ? String(numericNode.id) : "";
      next.categoryUrl = numericNode?.url || selectedNode?.url || "";

      if (!next.categoryId || next.categoryId !== prev.categoryId) {
        if (setExtraFieldValues) {
          setExtraFieldValues({});
        }
      }
      return next;
    });
  };

  const updateNodeChildren = (nodes, targetValue, children) =>
    nodes.map((node) => {
      const matches = String(node.id) === String(targetValue) || String(node.url) === String(targetValue);
      if (matches) {
        return { ...node, children };
      }
      if (node.children?.length) {
        return { ...node, children: updateNodeChildren(node.children, targetValue, children) };
      }
      return node;
    });

  const requestChildren = async (node) => {
    if (!node?.id && !node?.url) return;
    const targetValue = node.id || node.url;
    const params = new URLSearchParams();
    if (node.url) params.set("url", node.url);
    else params.set("id", node.id);
    if (newAd?.accountId) params.set("accountId", newAd.accountId);
    try {
      const response = await fetch(`http://localhost:5000/api/categories/children?${params.toString()}`);
      const data = await response.json();
      console.log("[AdModal] children response", {
        target: targetValue,
        count: Array.isArray(data?.children) ? data.children.length : 0,
        sample: Array.isArray(data?.children) ? data.children.slice(0, 5).map((item) => item?.id || item?.name) : []
      });
      if (!Array.isArray(data?.children) || !data.children.length) return;
      setLocalCategories((prev) => {
        const next = updateNodeChildren(prev, targetValue, data.children);
        const updated = findNode(next, targetValue);
        console.log("[AdModal] children applied", {
          target: targetValue,
          applied: updated?.children?.length || 0
        });
        return next;
      });
    } catch (error) {
      // ignore
    }
  };

  const getCategoryBreadcrumb = () => {
    const breadcrumb = [];
    let current = localCategories;

    for (const pathItem of categoryPath) {
      const found = current.find((cat) =>
        (cat.id && String(cat.id) === String(pathItem)) ||
        (cat.url && String(cat.url) === String(pathItem))
      );
      if (found) {
        breadcrumb.push(found);
        current = found.children || [];
      }
    }

    return breadcrumb;
  };

  const goBackInCategories = (index) => {
    if (index < 0) {
      setCategoryPath([]);
    } else {
      setCategoryPath(categoryPath.slice(0, index + 1));
    }
  };

  const currentCategories = getCurrentCategories();
  const breadcrumb = getCategoryBreadcrumb();

  return (
    <div style={{
      position: "fixed",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(15,23,42,0.7)",
      backdropFilter: "blur(4px)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      zIndex: 1000,
      animation: "fadeIn 0.2s ease-out"
    }}>
      <div style={{
        background: "linear-gradient(135deg, #0b1220 0%, #111827 100%)",
        borderRadius: "18px",
        padding: "30px",
        width: "720px",
        maxWidth: "92%",
        maxHeight: "92%",
        overflow: "auto",
        border: "1px solid rgba(148,163,184,0.2)",
        boxShadow: "0 30px 60px rgba(15,23,42,0.45)",
        color: "#e2e8f0",
        animation: "scaleIn 0.3s ease-out"
      }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "20px" }}>
          <div>
            <h2 style={{ margin: 0 }}>Создать объявление</h2>
            <p style={{ margin: "6px 0 0", color: "#94a3b8", fontSize: "14px" }}>
              Заполните поля и опубликуйте объявление в Kleinanzeigen
            </p>
          </div>
          <button
            onClick={onClose}
            style={{
              background: "none",
              border: "none",
              fontSize: "22px",
              cursor: "pointer",
              color: "#94a3b8",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              width: "32px",
              height: "32px",
              borderRadius: "8px",
              transition: "all 0.3s ease"
            }}
            onMouseEnter={(e) => e.currentTarget.style.background = "rgba(148,163,184,0.1)"}
            onMouseLeave={(e) => e.currentTarget.style.background = "none"}
          >
            <XIcon size={20} />
          </button>
        </div>

        <div style={{ display: "flex", flexDirection: "column", gap: "18px" }}>
          <div>
            <label style={labelStyle}>Аккаунт:</label>
            <select
              value={newAd.accountId}
              onChange={(e) => setNewAd((prev) => ({ ...prev, accountId: e.target.value }))}
              style={inputStyle}
            >
              <option value="">Выберите аккаунт</option>
              {accounts.map((account) => (
                <option key={account.id} value={account.id}>
                  {formatAccountLabel(account)} ({account.proxy || "Без прокси"})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label style={labelStyle}>Название объявления:</label>
            <input
              type="text"
              value={newAd.title}
              onChange={(e) => setNewAd((prev) => ({ ...prev, title: e.target.value }))}
              placeholder="Введите название (10-65 символов)"
              style={inputStyle}
              onFocus={(e) => e.currentTarget.style.borderColor = "rgba(59,130,246,0.5)"}
              onBlur={(e) => e.currentTarget.style.borderColor = "rgba(148,163,184,0.3)"}
            />
          </div>

          <div>
            <label style={labelStyle}>Описание:</label>
            <textarea
              value={newAd.description}
              onChange={(e) => setNewAd((prev) => ({ ...prev, description: e.target.value }))}
              placeholder="Введите описание (10-4000 символов)"
              rows="5"
              style={{
                ...inputStyle,
                minHeight: "140px",
                resize: "vertical",
                fontFamily: "inherit"
              }}
              onFocus={(e) => e.currentTarget.style.borderColor = "rgba(59,130,246,0.5)"}
              onBlur={(e) => e.currentTarget.style.borderColor = "rgba(148,163,184,0.3)"}
            />
          </div>

          <div>
            <label style={labelStyle}>Цена (EUR):</label>
            <div style={{ display: "flex", gap: "8px", alignItems: "center" }}>
              <input
                type="number"
                value={newAd.price}
                onChange={(e) => setNewAd((prev) => ({ ...prev, price: e.target.value }))}
                placeholder="0.00"
                style={inputStyle}
                onFocus={(e) => e.currentTarget.style.borderColor = "rgba(59,130,246,0.5)"}
                onBlur={(e) => e.currentTarget.style.borderColor = "rgba(148,163,184,0.3)"}
              />
              <span style={{ color: "#94a3b8", fontWeight: "600" }}>€</span>
            </div>
          </div>

          <div>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <label style={labelStyle}>Категория:</label>
              <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
                {categoriesUpdatedAt && (
                  <span style={{ fontSize: "12px", color: "#94a3b8" }}>
                    Обновлено: {new Date(categoriesUpdatedAt).toLocaleDateString()}
                  </span>
                )}
                <button
                  type="button"
                  onClick={() => onRefreshCategories(true)}
                  disabled={loadingCategories}
                  className="secondary-button"
                  style={{
                    padding: "6px 12px",
                    border: "1px solid rgba(148,163,184,0.3)",
                    cursor: loadingCategories ? "not-allowed" : "pointer",
                    fontSize: "12px",
                    display: "flex",
                    alignItems: "center",
                    gap: "6px"
                  }}
                >
                  {loadingCategories ? <LoadingIcon size={14} /> : <RefreshIcon size={14} />}
                  {loadingCategories ? "Обновление..." : "Обновить"}
                </button>
              </div>
            </div>

            <div style={{
              border: "1px solid rgba(148,163,184,0.3)",
              borderRadius: "12px",
              background: "rgba(15,23,42,0.7)",
              marginTop: "10px",
              overflow: "hidden"
            }}>
              <div style={{ padding: "12px 16px", borderBottom: "1px solid rgba(148,163,184,0.2)" }}>
                <div style={{ fontWeight: "600", color: "#e2e8f0" }}>Выберите категорию</div>
                <div style={{ fontSize: "12px", color: "#94a3b8" }}>Навигация по категориям</div>
              </div>

              {/* Хлебные крошки */}
              {breadcrumb.length > 0 && (
                <div style={{
                  padding: "10px 16px",
                  borderBottom: "1px solid rgba(148,163,184,0.2)",
                  display: "flex",
                  gap: "8px",
                  alignItems: "center",
                  flexWrap: "wrap",
                  fontSize: "12px",
                  color: "#94a3b8"
                }}>
                  <button
                    onClick={() => goBackInCategories(-1)}
                    style={{
                      background: "rgba(59,130,246,0.1)",
                      border: "1px solid rgba(59,130,246,0.3)",
                      borderRadius: "6px",
                      padding: "4px 8px",
                      cursor: "pointer",
                      color: "#7dd3fc",
                      fontSize: "12px",
                      transition: "all 0.2s ease"
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.background = "rgba(59,130,246,0.2)"}
                    onMouseLeave={(e) => e.currentTarget.style.background = "rgba(59,130,246,0.1)"}
                  >
                    Все категории
                  </button>
                  {breadcrumb.map((cat, index) => (
                    <React.Fragment key={cat.id || cat.url}>
                      <ChevronRightIcon size={12} />
                      <button
                        onClick={() => goBackInCategories(index)}
                        style={{
                          background: index === breadcrumb.length - 1 ? "rgba(59,130,246,0.2)" : "rgba(148,163,184,0.1)",
                          border: "1px solid " + (index === breadcrumb.length - 1 ? "rgba(59,130,246,0.3)" : "rgba(148,163,184,0.2)"),
                          borderRadius: "6px",
                          padding: "4px 8px",
                          cursor: index === breadcrumb.length - 1 ? "default" : "pointer",
                          color: index === breadcrumb.length - 1 ? "#7dd3fc" : "#e2e8f0",
                          fontSize: "12px",
                          transition: "all 0.2s ease"
                        }}
                      >
                        {cat.name}
                      </button>
                    </React.Fragment>
                  ))}
                </div>
              )}

              <div style={{ padding: "12px", minHeight: "220px", maxHeight: "300px", overflowY: "auto" }}>
                {currentCategories.length ? (
                  currentCategories.map((category) => {
                    const value = category.id || category.url || "";
                    const hasChildren = category.children && category.children.length > 0;
                    return (
                      <button
                        key={category.id || category.url || category.name}
                        type="button"
                        onClick={() => updateCategorySelection(category)}
                        disabled={!newAd.accountId}
                        style={{
                          width: "100%",
                          textAlign: "left",
                          padding: "10px 12px",
                          marginBottom: "6px",
                          borderRadius: "10px",
                          border: "1px solid transparent",
                          background: "rgba(148,163,184,0.05)",
                          color: "#e2e8f0",
                          cursor: newAd.accountId ? "pointer" : "not-allowed",
                          transition: "all 0.2s ease"
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.background = "rgba(59,130,246,0.1)";
                          e.currentTarget.style.borderColor = "rgba(59,130,246,0.3)";
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.background = "rgba(148,163,184,0.05)";
                          e.currentTarget.style.borderColor = "transparent";
                        }}
                      >
                        <span style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                          <span>{category.name}</span>
                          <ChevronRightIcon size={16} color="#94a3b8" />
                        </span>
                      </button>
                    );
                  })
                ) : (
                  <div style={{ color: "#94a3b8", padding: "12px", textAlign: "center" }}>
                    {!newAd.accountId
                      ? "Сначала выберите аккаунт"
                      : categoryPath.length > 0
                        ? "Нет подкатегорий - эта категория финальная"
                        : "Категории не найдены"}
                  </div>
                )}
              </div>

              <div style={{
                borderTop: "1px solid rgba(148,163,184,0.2)",
                padding: "10px 16px",
                color: "#94a3b8",
                fontSize: "12px",
                display: "flex",
                gap: "8px",
                flexWrap: "wrap",
                alignItems: "center"
              }}>
                <span style={{
                  background: "rgba(59,130,246,0.2)",
                  padding: "4px 8px",
                  borderRadius: "999px",
                  color: "#e2e8f0",
                  fontSize: "11px",
                  fontWeight: "600"
                }}>
                  Выбрано
                </span>
                {breadcrumb.length > 0 ? (
                  breadcrumb.map((cat, index) => (
                    <React.Fragment key={cat.id || cat.url}>
                      {index > 0 && <ChevronRightIcon size={12} />}
                      <span>{cat.name}</span>
                    </React.Fragment>
                  ))
                ) : (
                  <span>—</span>
                )}
              </div>
            </div>
          </div>

          {loadingExtraFields && (
            <div style={{ color: "#94a3b8", fontSize: "12px", display: "flex", alignItems: "center", gap: "8px" }}>
              <LoadingIcon size={14} />
              Загрузка параметров категории...
            </div>
          )}

          {!loadingExtraFields && extraFields?.length > 0 && (
            <div style={{ marginTop: "6px" }}>
              <label style={labelStyle}>Дополнительные параметры:</label>
              <div style={{ display: "grid", gap: "12px" }}>
                {extraFields.map((field) => {
                  const key = field.name || field.label;
                  const value = key ? (extraFieldValues?.[key] || "") : "";
                  return (
                    <div key={field.name || field.label} style={{ display: "flex", flexDirection: "column", gap: "6px" }}>
                      <span style={{ fontSize: "12px", color: "#94a3b8" }}>
                        {field.label}{field.required ? " *" : ""}
                      </span>
                      <select
                        value={value}
                        onChange={(e) => {
                          const nextValue = e.target.value;
                          setExtraFieldValues((prev) => ({ ...prev, [key]: nextValue }));
                        }}
                        style={inputStyle}
                      >
                        <option value="">Выберите</option>
                        {field.options?.map((option) => (
                          <option key={option.value || option.label} value={option.value}>
                            {option.label}
                          </option>
                        ))}
                      </select>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          <div>
            <label style={labelStyle}>PLZ (индекс):</label>
            <input
              type="text"
              value={newAd.postalCode}
              onChange={(e) => setNewAd((prev) => ({ ...prev, postalCode: e.target.value }))}
              placeholder="Введите почтовый индекс"
              style={inputStyle}
              onFocus={(e) => e.currentTarget.style.borderColor = "rgba(59,130,246,0.5)"}
              onBlur={(e) => e.currentTarget.style.borderColor = "rgba(148,163,184,0.3)"}
            />
          </div>

          <div>
            <label style={labelStyle}>Изображения:</label>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={(e) => setAdImages(Array.from(e.target.files || []))}
              style={{
                width: "100%",
                padding: "12px",
                border: "1px dashed rgba(148,163,184,0.4)",
                borderRadius: "12px",
                background: "rgba(15,23,42,0.7)",
                color: "#e2e8f0",
                cursor: "pointer",
                transition: "all 0.3s ease"
              }}
            />
            {adImages.length > 0 && (
              <div style={{ marginTop: "8px", fontSize: "12px", color: "#94a3b8" }}>
                Загружено файлов: {adImages.length}
              </div>
            )}
          </div>

          <div style={{ marginTop: "20px", display: "flex", gap: "10px", justifyContent: "flex-end" }}>
            <button
              type="button"
              onClick={onClose}
              className="secondary-button"
              style={{
                padding: "10px 20px",
                border: "none",
                cursor: "pointer"
              }}
            >
              Отмена
            </button>
            <button
              type="button"
              onClick={onSubmit}
              className="primary-button"
              style={{
                padding: "10px 20px",
                color: "white",
                border: "none",
                cursor: "pointer"
              }}
            >
              Создать объявление
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdModal;
