  useEffect(() => {
    let cancelled = false;
    const controller = new AbortController();

    const fetchExtraFields = async () => {
      if (!showAdModal) return;
      if (!newAd.accountId || (!newAd.categoryId && !newAd.categoryUrl)) {
        setLoadingExtraFields(false);
        setExtraFields([]);
        setExtraFieldValues({});
        setExtraFieldsError("");
        return;
      }

      setLoadingExtraFields(true);
      setExtraFieldsError("");
      const loadingGuardId = setTimeout(() => {
        if (cancelled) return;
        setLoadingExtraFields(false);
        setExtraFields([]);
        setExtraFieldValues({});
        setExtraFieldsError("Таймаут загрузки параметров категории. Попробуйте обновить категорию.");
      }, 60000);
      try {
        const params = new URLSearchParams();
        params.set("accountId", newAd.accountId);
        if (newAd.categoryId) params.set("categoryId", newAd.categoryId);
        if (!newAd.categoryId && newAd.categoryUrl) params.set("categoryUrl", newAd.categoryUrl);
        if (Array.isArray(newAd.categoryPath) && newAd.categoryPath.length > 0) {
          params.set("categoryPath", JSON.stringify(newAd.categoryPath));
        }
        const requestUrl = `/api/ads/fields?${params.toString()}`;
        const data = await apiFetchJson(requestUrl, {
          signal: controller.signal,
          timeoutMs: 0,
          retry: false
        });
        if (cancelled) return;
        if (data?.success === false) {
          const errorMessage = data?.error || "Ошибка загрузки параметров категории";
          setExtraFields([]);
          setExtraFieldValues({});
          setExtraFieldsError(errorMessage);
          return;
        }
        const fields = Array.isArray(data?.fields) ? data.fields : [];
        setExtraFields(fields);
        setExtraFieldsError("");
        const nextValues = {};
        fields.forEach((field) => {
          const key = field.name || field.label;
          if (!key) return;
          nextValues[key] = extraFieldValues[key] || "";
        });
        setExtraFieldValues(nextValues);
      } catch (error) {
        if (cancelled) return;
        if (error?.name === "AbortError") return;
        handleAuthError(error);
        setExtraFields([]);
        setExtraFieldValues({});
        const errorMessage = error?.message || "Ошибка загрузки параметров категории";
        setExtraFieldsError(errorMessage);
      } finally {
        clearTimeout(loadingGuardId);
        if (cancelled) return;
        setLoadingExtraFields(false);
      }
    };
    fetchExtraFields();
    return () => {
      cancelled = true;
      controller.abort();
    };
  }, [showAdModal, newAd.accountId, newAd.categoryId, newAd.categoryKey, newAd.categoryUrl, newAd.categoryPath]);

