              🔒
            </div>
            <h2 className="token-gate-title" style={{ margin: 0, fontSize: "22px" }}>Требуется токен доступа</h2>
            <p className="token-gate-desc" style={{ margin: "8px 0 18px", color: "#94a3b8", fontSize: "14px" }}>
              Введите действующий токен, чтобы разблокировать функции менеджера.
            </p>

            <div className="token-gate-form" style={{
              display: "flex",
              gap: "10px",
              flexDirection: isPhoneView ? "column" : "row"
            }}>
              <input
                className="token-gate-input"
                type="password"
                placeholder="Введите токен"
                value={accessTokenDraft}
                onChange={(e) => setAccessTokenDraft(e.target.value)}
                style={{
                  flex: 1,
                  padding: "12px 14px",
                  borderRadius: "12px",
                  border: "1px solid rgba(148,163,184,0.3)",
                  background: "rgba(15,23,42,0.7)",
                  color: "#e2e8f0",
                  fontSize: "14px",
                  width: isPhoneView ? "100%" : "auto"
                }}
              />
              <button
                className="token-gate-submit"
                onClick={applyAccessToken}
                style={{
                  padding: "12px 16px",
                  borderRadius: "12px",
                  border: "none",
                  cursor: "pointer",
                  background: "linear-gradient(135deg, #2563eb 0%, #7c3aed 100%)",
                  color: "white",
                  fontWeight: "600",
                  fontSize: "14px",
                  whiteSpace: isPhoneView ? "normal" : "nowrap",
                  width: isPhoneView ? "100%" : "auto"
                }}
              >
                Применить токен
              </button>
            </div>

            <div className="token-gate-status" style={{
              marginTop: "12px",
              fontSize: "12px",
              color: tokenStatus.state === "missing" ? "#94a3b8" : "#fca5a5"
            }}>
              {tokenStatus.message || " "}
            </div>
          </div>
        </div>
      )}

      {/* Navigation */}
      <div className="app-nav" style={{
        background: "linear-gradient(180deg, rgba(15,23,42,0.9) 0%, rgba(15,23,42,0.8) 100%)",
        marginTop: "1px",
        borderBottom: "1px solid rgba(148,163,184,0.12)"
      }}>
        <div className="app-nav-inner" style={{
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "8px 24px",
          gap: "8px"
        }}>
          {isPhoneView ? (
            <>
              <button
                type="button"
                className="mobile-nav-toggle"
                aria-label="Открыть меню разделов"
                aria-expanded={isMobileNavOpen}
                onClick={() => setIsMobileNavOpen((prev) => !prev)}
              >
                ≡
                <span>Разделы</span>
              </button>
              {isMobileNavOpen && (
                <div className="app-nav-mobile-panel" style={{ display: "flex", flexDirection: "column", gap: "8px", width: "100%" }}>
                  {tabs.map(tab => (
                    <button
                      key={tab.id}
                      onClick={() => handleTabSelect(tab.id)}
                      className={`nav-button ${activeTab === tab.id ? "active" : ""}`}
                      style={{
                        width: "100%",
                        justifyContent: "flex-start",
                        padding: "12px 14px",
                        borderRadius: "12px",
                        cursor: "pointer",
                        fontSize: "14px",
                        fontWeight: "700",
                        display: "flex",
                        alignItems: "center",
                        gap: "10px",
                        background: "transparent",
                        border: "none",
                        boxShadow: "none",
                        color: "#ffffff",
                        WebkitTextFillColor: "#ffffff"
                      }}
                    >
                      <span style={{ color: activeTab === tab.id ? "#a78bfa" : tab.color }}>{tab.icon}</span>
                      {tab.label}
                    </button>
                  ))}
                </div>
              )}
            </>
          ) : (
            <div className="app-nav-list">
              {tabs.map(tab => (
              <button
                key={tab.id}
                onClick={() => handleTabSelect(tab.id)}
                className={`nav-button ${activeTab === tab.id ? 'active' : ''}`}
                style={{
                  padding: "12px 20px",
                  borderRadius: "9999px",
                  cursor: "pointer",
                  fontSize: "14px",
                  fontWeight: "700",
                  display: "flex",
                  alignItems: "center",
                  gap: "10px",
                  whiteSpace: "nowrap",
                  background: "transparent",
                  border: "none",
                  boxShadow: "none",
                  color: "#ffffff",
                  WebkitTextFillColor: "#ffffff"
                }}
              >
                <span style={{ color: activeTab === tab.id ? "#a78bfa" : tab.color }}>{tab.icon}</span>
                {tab.label}
              </button>
            ))}
            </div>
          )}
        </div>
      </div>

      {/* Content */}
      <div className="app-content" style={{
        maxWidth: "1200px",
        margin: "24px auto 0",
        padding: "0 24px",
        paddingBottom: "48px"
      }}>
        {activeTab === "dashboard" && <DashboardTab />}
        {activeTab === "accounts" && <AccountsTab />}
        {activeTab === "proxies" && <ProxiesTab />}
        {activeTab === "messages" && <MessagesTab />}
        {activeTab === "active-ads" && <ActiveAdsTab />}

      </div>

      {/* Модальные окна */}
      {showAddAccountModal && (
        <AddAccountModal
          isOpen={showAddAccountModal}
          onClose={() => setShowAddAccountModal(false)}
          onSuccess={handleAddAccount}
          proxies={proxies}
        />
      )}

      {showAddProxyModal && (
        <AddProxyModal
          isOpen={showAddProxyModal}
          onClose={() => setShowAddProxyModal(false)}
          onSuccess={handleAddProxy}
        />
      )}

      {showAdModal && (
        <AdModal
          isOpen={showAdModal}
          onClose={() => setShowAdModal(false)}
          onSubmit={handleCreateAd}
          publishing={publishingAd}
          accounts={accounts}
          categories={categories}
          categoriesUpdatedAt={categoriesUpdatedAt}
          onRefreshCategories={loadCategories}
          loadingCategories={loadingCategories}
          extraFields={extraFields}
          extraFieldValues={extraFieldValues}
          setExtraFieldValues={setExtraFieldValues}
          loadingExtraFields={loadingExtraFields}
          extraFieldsError={extraFieldsError}
          newAd={newAd}
          setNewAd={setNewAd}
          adImages={adImages}
          setAdImages={setAdImages}
        />
      )}
    </div>
  );
}

export default App;
