        <span style={{ fontSize: "12px", color: "#52c41a" }}>
          CONNECT OK ({connection.connectTime}мс{connection.statusCode ? `, ${connection.statusCode}` : ""})
        </span>
      );
    }

    return (
      <span style={{ fontSize: "12px", color: "#f87171" }}>
